const initState = { 
    authError: null
}

const authReducer = (state = initState, action) => {
    switch(action.type){
        case 'LOGIN_ERROR':
        return {
            ...state,
            authError: 'Login Failed'
        }
        case 'LOGIN_SUCCESS':
            console.log('loginSuccess');
            return{
                ...state,
                authError: null
            }
        default:
        return state;
    }
    return state
}

export default authReducer
